package c.config;import static org.springframework.context.annotation.ComponentScan.Filter;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.config.PropertyPlaceholderConfigurer;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;import org.springframework.core.io.ClassPathResource;import org.springframework.stereotype.Controller;import ch.qos.logback.classic.Logger;import c.Application;//mówi, że to jest config@Configuration// mówi, gdzie szukać dalszych configów@ComponentScan(basePackageClasses = Application.class, excludeFilters = @Filter({		Controller.class, Configuration.class }))public class ApplicationConfig {	public static Logger rootLogger = (Logger) LoggerFactory.getLogger("mainLogger");		@Bean	public static PropertyPlaceholderConfigurer propertyPlaceholderConfigurer() {		PropertyPlaceholderConfigurer ppc = new PropertyPlaceholderConfigurer();		ppc.setLocation(new ClassPathResource("/persistence.properties"));		return ppc;	}}